mapper.select:
/* queryId = selectPostList */ SELECT ALL_LIST_MST.* FROM ( SELECT ROWNUM PAGE_RN , ALL_LIST_DTL.* FROM ( SELECT ROWNUM RN, BBS_CATALOG.BBS_CATAL_NM, BBS_POST.BBS_CATAL_ID, BBS_POST.BBS_POST_ID, BBS_POST.BBS_POST_TITLE, (SELECT COUNT(1) FROM BBS_REPLE WHERE BBS_REPLE.BBS_POST_ID = BBS_POST.BBS_POST_ID AND BBS_REPLE.BBS_REPLE_DEL_STAT = 'N') AS POST_REPLE_CNT, (SELECT COUNT(1) FROM BBS_VIEWS WHERE BBS_POST.BBS_POST_ID = BBS_POST_ID) AS BBS_VIEW_CNT, BBS_POST.BBS_POST_REG_DATE, (SELECT IRON_USER.USER_NM FROM IRON_USER WHERE IRON_USER.USER_ID = BBS_POST.BBS_POST_REG_ID) AS BBS_POST_WRITER, BBS_POST.BBS_POST_REG_ID, BBS_POST.BBS_POST_MOD_DATE, BBS_POST.BBS_POST_MOD_ID, BBS_POST.BBS_POST_STATUS FROM BBS_POST, BBS_CATALOG, IRON_USER WHERE 1 = 1 AND BBS_POST.BBS_CATAL_ID = BBS_CATALOG.BBS_CATAL_ID AND BBS_POST.BBS_POST_REG_ID = IRON_USER.USER_ID(+) AND BBS_POST.BBS_POST_STATUS = 'N'
AND BBS_POST.BBS_CATAL_ID = #{bbsCatalId}
AND ( BBS_POST.BBS_POST_TITLE LIKE '%' || #{selAllKey} || '%' OR BBS_POST.BBS_POST_CONTENT LIKE '%' || #{selAllKey} || '%' )
AND ( BBS_POST.BBS_POST_TITLE LIKE '%' || #{selSrcKey} || '%' OR BBS_POST.BBS_POST_CONTENT LIKE '%' || #{selSrcKey} || '%' OR BBS_POST.BBS_POST_REG_ID LIKE '%' || #{selSrcKey} || '%' )
AND ( BBS_POST.BBS_POST_TITLE LIKE '%' || #{selSrcKey} || '%' OR BBS_POST.BBS_POST_CONTENT LIKE '%' || #{selSrcKey} || '%' )
AND IRON_USER.USER_NM LIKE '%' || #{selSrcKey} || '%'
ORDER BY BBS_POST.BBS_POST_REG_DATE DESC ) ALL_LIST_DTL ) ALL_LIST_MST WHERE 1 = 1 AND ALL_LIST_MST.PAGE_RN BETWEEN ((#{pageNumInfo} - 1) * #{pageRowInfo}) + 1 AND #{pageNumInfo} * #{pageRowInfo}
AND ALL_LIST_MST.BBS_POST_REG_DATE BETWEEN #{startDt} AND #{endDt}

/* queryId = selectPostCatalInfo */
SELECT BBS_CATAL_ID , BBS_CATAL_NM , BBS_CATAL_USE_YN , BBS_CATAL_REG_DATE , BBS_CATAL_REG_ID , BBS_CATAL_MOD_DATE , BBS_CATAL_MOD_ID FROM BBS_CATALOG WHERE BBS_CATAL_USE_YN = 'Y'
AND BBS_CATAL_ID = #{bbsCatalId}

mapper.insert:
/* queryId = insertPostInfo */
INSERT INTO BBS_POST( BBS_CATAL_ID, BBS_POST_ID, BBS_POST_TITLE, BBS_POST_CONTENT, BBS_POST_REG_DATE, BBS_POST_REG_ID, BBS_POST_MOD_DATE, BBS_POST_MOD_ID, BBS_POST_STATUS ) SELECT #{bbsCatalId} AS BBS_CATAL_ID, ('BP' || NVL(SUBSTR(MAX(BBS_POST_ID),3,8), TO_CHAR(SYSDATE,'YYYYMMDD')) || CASE WHEN SUBSTR(MAX(BBS_POST_ID),11,3) IS NULL THEN '001' ELSE LPAD(TO_CHAR(TO_NUMBER(SUBSTR(MAX(BBS_POST_ID),11,13))+1), 3, '0') END ) AS BBS_POST_ID, #{bbsPostTitle} AS BBS_POST_TITLE, EMPTY_CLOB() AS BBS_POST_CONTENT, TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') AS BBS_POST_REG_DATE, #{bbsPostRegId} AS BBS_POST_REG_ID, TO_CHAR(SYSDATE,'YYYYMMDDHH24MISS') AS BBS_POST_MOD_DATE, #{bbsPostRegId} AS BBS_POST_MOD_ID, 'N' AS BBS_POST_STATUS FROM BBS_POST WHERE SUBSTR(BBS_POST_ID, 3, 8) = TO_CHAR(SYSDATE, 'YYYYMMDD')
SELECT MAX(BBS_POST_ID) AS bbsPostId FROM BBS_POST

mapper.select:
/* queryId = selectAttachListList */
SELECT BBS_POST_ID , BBS_ATTACH_ID , BBS_ATTACH_SN , BBS_ATTACH_LOC , BBS_ATTACH_SAVE_NM , BBS_ATTACH_ORIGIN_NM FROM BBS_ATTACH WHERE 1 = 1
AND BBS_POST_ID = #{bbsPostId}
AND BBS_ATTACH_ID = #{bbsAttachId}
ORDER BY BBS_ATTACH_ID, BBS_ATTACH_SN

mapper.update:
/*
	queryId = deletePostInfo
*/

UPDATE 	BBS_POST 
   SET 	BBS_POST_STATUS = 'Y'
 WHERE	BBS_POST_ID = #{bbsPostId}

/*
	queryId = deletePostRepleInfo
*/

UPDATE 	BBS_REPLE 
   SET 	BBS_REPLE_DEL_STAT = 'Y'
 WHERE	BBS_POST_ID = #{bbsPostId}
   AND  BBS_REPLE_ID = #{bbsRepleId}

mapper.select:
/*
	queryId = selectUserPostViewCnt
*/

SELECT COUNT(BBS_VIEW_ID)
  FROM BBS_VIEWS
 WHERE BBS_POST_ID = #{bbsPostId}


   AND BBS_POST_VIEW_USER_ID = #{userId}

mapper.delete:
/*
	queryId = deleteAttachInfo
*/

DELETE FROM BBS_ATTACH
 WHERE BBS_POST_ID = #{bbsPostId}
   AND BBS_ATTACH_ID = #{bbsAttachId}
