mapper.select:
/*
query id = signMapperDao.signUpIdChk
*/
SELECT COUNT(USER_ID)
  FROM IRON_USER
 WHERE USER_ID = #{userId}

mapper.insert:
/*
query id = signMapperDao.signUpProcess
*/
INSERT INTO IRON_USER(
		  USER_ID
	    , USER_PW
	    , USER_NM
	    , GENDER
		, BIRTH
		, MAIL
		, PHONE
		, RESIGN_YN
		, ADDR
		, ADDR_DETAIL
		, ZIP_CODE
		, REG_DATE
		, REG_ID
		, MODI_DATE
		, MODI_ID
)
VALUES(
		  #{userId}
	    , #{userPw}
	    , #{userNm}
	    , '1'
		, #{userBirth}
		, #{userMail}
		, #{userPhone}
		, 'N'
		, #{userAddr}
		, #{userAddrDetail}
		, #{zipCode}
		, TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
		, #{userId}
		, TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
		, #{userId}
)
